.container {
    width: 100%; /* Ensure container takes up the full width of the screen */
    max-width: 100%; /* Allow container to expand to the full width */
    margin: 0 auto;
    padding: 20px;
    font-family: 'Arial', sans-serif;
    display: flex;
    flex-direction: column;
    align-items: center;
  }
  
  /* Restaurant name styling */
  .res-name {
    font-size: 2rem;
    font-weight: bold;
    margin-bottom: 20px;
    text-align: center;
    color: #333;
  }
  
  /* Search input field styling */
  input[type="text"],
  input[type="search"] {
    width: 100%; /* Ensures the search input takes the full width */
    padding: 10px;
    margin: 10px 0 20px;
    border: none; /* Removed the border */
    border-radius: 8px;
    font-size: 1rem;
    box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  }
  
